package lesson1;

public class HelloWorld {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 1;
		while(x<3) {
			System.out.println("Doo");
			System.out.println("Bee");
			x=x+1;
		}
		if(x==3) {
			System.out.println("Do");
		}
		System.out.println("Hello World");

	}

}
