package lesson1;

public class Shuffle {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 3;
		
		while(x>0) {
		
			if(x>2) {
				System.out.println("a");
			}
			
			x=x-1;
			System.out.println("-");
			
			if(x==2) {
				System.out.println("b c");
			}
			
			if(x==1) {
				System.out.println("d");
			}
			
				
		}
		
	}

}
